mapper.select:
select 

from d_caja_det_tbl
where uid_actividad = #{uidActividad,jdbcType=VARCHAR}
  and uid_diario_caja = #{uidDiarioCaja,jdbcType=VARCHAR}
  and linea = #{linea,jdbcType=INTEGER}

mapper.delete:
delete from d_caja_det_tbl
where uid_actividad = #{uidActividad,jdbcType=VARCHAR}
  and uid_diario_caja = #{uidDiarioCaja,jdbcType=VARCHAR}
  and linea = #{linea,jdbcType=INTEGER}
